import React, { useCallback, useEffect, useState } from "react";
import AppLayout from "../components/AppLayout";
import { Form, Input, Checkbox, Button } from "antd";
import Head from "next/head";
import styled from "styled-components";
import useinput from "../hooks/useinput";
import { SIGN_UP_REQUEST } from "../reducers/user";
import { useDispatch, useSelector } from "react-redux";
import router from "next/router";

const ErrorMessage = styled.div`
  color: red;
`;

const Signup = () => {
  const dispatch = useDispatch();
  const { signUpLoading, signUpDone, signUpError } = useSelector((state) => state.user);

  useEffect(() => {
    if (signUpDone) {
      router.push("/"); //완료되면 메인페이지로
    }
  }, [signUpDone]);

  useEffect(() => {
    if (signUpError) {
      alert(signUpError);
    }
  }, [signUpError]);

  const [Email, onChangeEmail] = useinput("");
  // const [Id, setId] = useState("");
  // const onChangeId = useCallback((e) => {
  //   setId(e.target.value);
  // }, []);
  const [NickName, onChangeNickName] = useinput("");
  // const [NickName, setNickName] = useState("");
  // const onChangeNickName = useCallback((e) => {
  //   setNickName(e.target.value);
  // }, []);
  const [Password, onChangePassword] = useinput("");
  // const [Password, setPassword] = useState("");
  // const onChangePassword = useCallback((e) => {
  //   setPassword(e.target.value);
  // }, []);
  const [PasswordError, setPasswordError] = useState(false); //비밀번호가 일치 하지 않으면 첫번쨰 true가 됨
  const [PasswordCheck, setPasswordCheck] = useState("");
  const onChangePasswordCheck = useCallback(
    (e) => {
      setPasswordCheck(e.target.value);
      setPasswordError(e.target.value !== Password);
    },
    [Password]
  );
  const [Term, setTerm] = useState("");
  const [TermError, setTermError] = useState(false);
  const onChangeTerm = useCallback((e) => {
    setTerm(e.target.checked);
    setTermError(false);
  }, []);

  const onsubmit = useCallback(() => {
    if (Password !== PasswordCheck) {
      return setPasswordError(true);
    }
    if (!Term) {
      return setTermError(true);
    }
    console.log(Email, NickName, Password);
    dispatch({
      type: SIGN_UP_REQUEST,
      data: { Email, Password, NickName },
    });
  }, [Email, Password, PasswordCheck, Term]);
  return (
    <AppLayout>
      <Head>
        <title>회원가입</title>
      </Head>
      <Form onFinish={onsubmit}>
        <div>
          <label htmlFor="user-email">이메일</label>
          <br />
          <Input name="user-email" type="email" value={Email} onChange={onChangeEmail} required />
        </div>
        <div>
          <label htmlFor="user-id">닉네임</label>
          <br />
          <Input name="user-id" value={NickName} onChange={onChangeNickName} required />
        </div>
        <div>
          <label htmlFor="user-password">비밀번호</label>
          <br />
          <Input name="user-password" type="password" value={Password} onChange={onChangePassword} required />
        </div>
        <div>
          <label htmlFor="user-password">비밀번호 체크</label>
          <br />
          <Input
            name="user-password-check"
            type="password"
            value={PasswordCheck}
            onChange={onChangePasswordCheck}
            required
          />
          {PasswordError && <ErrorMessage>비밀번호가 일치하지 않습니다.</ErrorMessage>}
        </div>
        <div>
          <Checkbox name="user-term" checked={Term} onChange={onChangeTerm}>
            내 말을 잘 들을것
          </Checkbox>
          {TermError && <ErrorMessage> 약관에 동의하셔야 합니다.</ErrorMessage>}
        </div>
        <div style={{ marginTop: 10 }}>
          <Button type="primary" htmlType="submit" loading={signUpLoading}>
            가입하기
          </Button>
        </div>
      </Form>
    </AppLayout>
  );
};

export default Signup;

//TermError는 제출할때 true가 됨
//Button type="primary"
//htmlType="submit" 할시 위에 onFinish가 호출됨. e.preventDefault() 내장 되어 있어서 안해도됨
