export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "제로초",
      },
      content: "첫번째 게시글 #해시태그, #익스프레스",
      Image: [
        {
          src: "https://www.ilbe.com",
        },
        {
          src: "https://www.naver.com",
        },
      ],
      Comments: [{}],
    },
  ],
};
//왜 User, Image, Comment만 첫글자에 대문자냐?

const reducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default reducer;
